SELECT * FROM DELIVERIES
WHERE 1 = 1

	AND delivery_id LIKE '%' || #{searchKeyword} || '%'


	AND order_id LIKE '%' || #{searchKeyword} || '%'


	AND delivery_date LIKE '%' || #{deliveryDate} || '%'


	AND SUBSTR(delivery_address, 1, INSTR(delivery_address, '
	') - 1) = #{addressSystem}


